import React from "react";
import { render } from "@testing-library/react";

export default class App extends React.Component{
    constructor(props){
        super(props)
            this.state={
                todos:[],
                input:""
            }
        }

    handleChange=(value)=>{
        this.setState({input:value})
    }
    handleSubmit=(e)=>{
        e.preventDefault();
        this.setState({todos:[...this.state.todos,{id:Date.now(),text:this.state.input,isDone:false}],input:""})

    }
    handleRemove=(id)=>{
        let  Filter=this.state.todos.filter((todo)=>todo.id !== id)
        this.setState({todos:Filter})
    }
    handleToggle=(id)=>{
        let Mapped=this.state.todos.map(todo=>{
            if(todo.id ===id){
                todo.isDone = !todo.isDone
            }
            return todo
        })
        this.setState({todos:Mapped})
    }
    render(){
        console.log(this.state.input,"rendering");
        return(
            <div>
                <form onSubmit={this.handleSubmit}>

                <input value={this.state.input} onChange={({target:{value}})=>this.handleChange(value)}/>
                </form>
                <ul>
                    {this.state.todos.map((todo,i)=>{
                        return(
                        <li key={i}>
                            <input type='checkbox' checked={todo.isDone} onChange={()=>this.handleToggle(todo.id)}/>
                            <p>{todo.text}</p>
                            <button onClick={()=>this.handleRemove(todo.id)}>X</button>
                        </li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}
