import React from "react";
import { render } from "@testing-library/react";

export default class App extends React.Component{
    constructor(props){
        super(props)
            this.state={
                todos:[],
                input:""
            }
        }

    render(){
        return(
            <div>
                <input placeholder={this.state.input}/>
                <ul>
                    {this.state.todos.map((todo,i)=>{
                        return(
                        <li>{todo}</li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}
