import React from "react";
import { render } from "@testing-library/react";

export default class App extends React.Component{
    constructor(props){
        super(props)
            this.state={
                todos:[],
                input:""
            }
        }

    handleChange=(value)=>{
        this.setState({input:value})
    }
    handleSubmit=(e)=>{
        if(e.keyCode===13){

            console.log(e.target,"target");
            this.setState({todos:[...this.state.todos,{id:Date.now(),text:this.state.input,isDone:false}]})
        }

    }
    handleRemove=(id)=>{
       let  Filter=this.state.todos.filter((todo)=>todo.id !== id)
       this.setState({todos:Filter})
    }
    render(){
        return(
            <div>

                <input value={this.state.input} onSubmit={()=>this.handleSubmit(e)} onChange={({target:{value}})=>this.handleChange(value)}/>

                <ul>
                    {this.state.todos.map((todo,i)=>{
                        return(
                        <li>
                            <input type='checkbox' checked={todo.isDone}/>
                            <p>{todo.text}</p>
                            <button onClick={()=>this.handleRemove(todo.id)}>X</button>
                        </li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}
