import React from "react";
import { render } from "@testing-library/react";

export default class App extends React.Component{
    constructor(props){
        super(props)
            this.state={
                todos:[],
                input:""
            }
        }

    handleChange=(value)=>{
        this.setState({input:value})
    }
    handleSubmit=(e)=>{
        e.preventDefault();
        e.target=""
        this.setState({todos:[...this.state.todos,this.state.input]})

    }
    handleRemove=(i)=>{
       let  Filter=this.state.todos.filter((todo,index)=>index !==i)
       console.log(Filter,"mila");
    }
    render(){
        console.log(this.state.input);
        console.log(this.state.todos);
        return(
            <div>
                <form onSubmit={this.handleSubmit}>

                <input value={this.state.input} onChange={({target:{value}})=>this.handleChange(value)}/>
                </form>
                <ul>
                    {this.state.todos.map((todo,i)=>{
                        return(
                        <li>
                            <input type='checkbox'/>
                            <p>{todo}</p>
                            <button onClick={(i)=>this.handleRemove(i)}>X</button>
                        </li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}
