import React from "react";
import { render } from "@testing-library/react";

export default class App extends React.Component{
    constructor(props){
        super(props)
            this.state={
                todos:[],
                input:""
            }
        }

    handleChange=(value)=>{
        this.setState({input:value})
    }
    handleSubmit=(e)=>{
        e.preventDefault();
        this.setState({todo:[...this.state.todos,this.state.input]})
    }

    render(){
        console.log(this.state.input);
        return(
            <div>
                <form onSubmit={(e)=>this.handleSubmit(e)}>

                <input value={this.state.input} onChange={({target:{value}})=>this.handleChange(value)}/>
                </form>
                <ul>
                    {this.state.todos.map((todo,i)=>{
                        return(
                        <li>{todo}</li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}
