import React from "react";
// import { render } from "@testing-library/react";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      filterTodos:[]||[...this.state.todos],
      input: "",
      active: [],
      completed : []
    };

  }


  handleChange = (value) => {
    this.setState({ input: value });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      todos: [
        ...this.state.todos,
        { id: Date.now(), text: this.state.input, isDone: false },
      ],
      input: "",
      active:[...(this.state.todos.filter(todo => !todo.isDone))],
      completed:[...(this.state.todos.filter(todo => !todo.isDone))]

    });
  };
  handleRemove = (id) => {
    let Filter = this.state.todos.filter((todo) => todo.id !== id);
    this.setState({ todos: Filter });
  };
  handleToggle = (id) => {
    let Mapped = this.state.todos.map((todo) => {
      if (todo.id === id) {
        todo.isDone = !todo.isDone;
      }
      return todo;
    });
    this.setState({ todos: Mapped });
    // this.setState({todos.completed : this.state.todos.filter(todo => todo.isDone)});
  };

  handleAll = (todos) => {
       this.setState({filterTodos:this.state.todos});
  }
  handleCompleted = (state) => {

    let completed = this.state.todos.filter(todo => todo.isDone);
    this.setState( {filterTodos : completed});

  }
  handleActive = (state) => {
    let active = this.state.todos.filter(todo => !todo.isDone);
    this.setState( {filterTodos: active});
  }
  render() {
    // console.log(this.state.input,"rendering");

    return (
      <div>
        <h2>todos</h2>
        <div>
          <form onSubmit={this.handleSubmit}>
            <input
              value={this.state.input}
              onChange={({ target: { value } }) => this.handleChange(value)}
            />
          </form>
          <ul>
            {this.state.filterTodos.map((todo, i) => {
              return (
                <li key={i}>
                  <input
                    type="checkbox"
                    checked={todo.isDone}
                    onChange={() => this.handleToggle(todo.id)}
                  />
                  <p>{todo.text}</p>
                  <span onClick={() => this.handleRemove(todo.id)}>X</span>
                </li>
              );
            })}
          </ul>
        </div>
        <footer>
          <span class="item">0 item left</span>
          <button class="all" onClick={() => this.handleAll(this.state.todos)}>All</button>
          <button class="completed" onClick={() => this.handleCompleted(this.state.todos)}>Completed</button>
          <button class="active" onClick={() => this.handleActive(this.state.todos)}>Active</button>
          <button class="clear">
            <a class="anchor" href="##">
              Clear Completed
            </a>
          </button>
        </footer>
      </div>
    );
  }
}
